y_predicted = model.predict(x_test_norm)
mat = confusion_matrix(y_test_encoded.argmax(axis=1), y_predicted.argmax(axis=1))
class_names = ['class1', 'class2', 'class3', 'class4']

# Normalize the confusion matrix
