sns.heatmap(normalized_mat, annot=True, cmap='Blues', cbar=False, 
            xticklabels=class_names, yticklabels=class_names,
            square=True, fmt='.2f', annot_kws={"fontsize":16})

# Set the tick labels and tick locations
